import {useEffect, useState, useRef} from 'react';

function List() {
    
    const [list, setList] = useState([]);

    useEffect(()=>{
        fetch("http://127.0.0.1:8081/gettasks")
        .then(response => response.json())
        .then(setList);
    }, []);

    function handleCheckUncheck() {

    }

    return (
        <h2>List of tasks:
        <div style={{display: 'flex', 'flex-wrap': 'wrap' }}>
           {list.map(todo => 
            <div style={{border: '1px solid black', margin: '5px', padding: '3px'}} key={todo.id}>
                <input type={'checkbox'} onChange={handleCheckUncheck} id={'cb_' + todo.id} />  
                {todo.description}
                <button>edit</button>
                <button>del</button>
            </div>)}
         </div>
         </h2>
       );
    
}

export default List;
