import React from "react";
import {useEffect, useState, useRef} from 'react';

function List() {
    
    const [list, setList] = useState([]);

    inpChkBox = React.createRef();

    useEffect(()=>{
        fetch("http://127.0.0.1:8081/getUnfinishedTasks")
        .then(response => response.json())
        .then(setList);
    }, []);

    function handleCheckUncheck() {

        const inputChkBox = this.myRef.current;
        console.log(inputChkBox);
        // fetch("http://127.0.0.1:8081/markFinished", {
        //     method: 'PUT',
        //     body: JSON.stringify(null)
        // })
        // .then(response => response.json())
        // .then();

    }

    return (
        
        <div style={{display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between'}}>
           {list.map(todo => 
            <div style={{border: '1px solid grey', margin: '10px', 'padding': '3px', 
            'padding-right': '5px', 'padding-bottom': '5px'}}  
            key={todo.id} >
                <input type={'checkbox'} ref={inpChkBox} onChange={handleCheckUncheck} id={'cb_' + todo.id} />  
                {todo.description}
                <button className={'modifybuttons'}>edit</button>
                <button className={'modifybuttons'}>del</button>
            </div>)}
         </div>
       );
    
}

export default List;
