import React, { Component } from "react";



function TaskList() {
    
    // let listitems;

    return fetch("http://127.0.0.1:8081/gettasks").then(response => {
        return response.text();
      }).then(data => {
        var data_json = JSON.parse(data);
        var listitems = data_json.map((data_json) => <li> {data_json['description']} </li>);     
        return (<ul>{ listitems }</ul>) ;

    });

    // return <ul>{ listitems }</ul> ;
    
}

export default TaskList;
