import React from "react";
import {useEffect, useState, useRef} from 'react';

function List() {
    
    const [list, setList] = useState([]);

    useEffect(()=>{
        fetch("http://127.0.0.1:8081/getUnfinishedTasks")
        .then(response => response.json())
        .then(setList);
    }, []);

    function handleCheckUncheck() {


    }

    return (
        
        <div style={{display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between'}}>
           {list.map(todo => 
            <div style={{border: '1px solid grey', margin: '10px', padding: '3px', 
            paddingRight: '5px', paddingBottom: '5px'}}  
            key={todo.id} >
                <input  type={'checkbox'} onChange={handleCheckUncheck} key={todo.id} />  
                {todo.description}
                <button className={'modifybuttons'}>edit</button>
                <button className={'modifybuttons'}>del</button>
            </div>)}
         </div>
       );
    
}

export default List;
