import React, { Component } from "react";

class AddToDoForm extends Component {

    constructor(props) {
        super(props);
        this.state = {
          values: {
            task: ""
          },
          isSubmitting: false,
          isError: false
        };
    }

    submitForm = e => {
        console.log("submit form called");
        e.preventDefault();
        this.setState({ isSubmitting: true });
        console.log(this.state.values);
        fetch("http://127.0.0.1:8081/addtodo", {
            method: "POST",
            body: JSON.stringify(this.state.values),
            headers: {
                "Content-Type": "application/json"
            }
        })
        .then(res => {
            this.setState({ isSubmitting: false });
            return res.json();
        })
        .then(data => {
            console.log(data);
            if(!data.hasOwnProperty("error")){
                this.setState({ message: data.success });
                // re-render list component here
            }
            else {
                this.setState({ message: data.error, isError: true });
            }


        });

        window.location.reload(false);
    }

    handleInputChange = e =>
    this.setState({
      values: { ...this.state.values, [e.target.name]: e.target.value }
    });

    render() {
        return (
            <form onSubmit={this.submitForm} autoComplete={'off'}>
                <label>Add a new task here : </label>
                <input type="text" name="task" id = "task" value={this.state.values.task} onChange={this.handleInputChange}/>
                <button style={{margin: '5px'}} type="submit">Add</button>
            </form>
        );
    }
}  

export default AddToDoForm;
