import React, { Component } from "react";



function TaskList() {
    
    listitems = fetch("http://127.0.0.1:8081/gettasks").then(response => {
        return response.text();
      }).then(data => {
        return data.map((data) => <li>{data}</li>);     
      });

    return <ul>{ listitems }</ul> ;
}

export default TaskList;
