import React from "react";
import {useEffect, useState, useRef} from 'react';
import TaskItem from "./TaskItem";

function List(props) {
    
    const [list, setList] = useState([]);
    var api_url;
    
    if (props.type === "unfinished")
        api_url = "http://127.0.0.1:8081/getUnfinishedTasks"
    else if (props.type === "finished")
        api_url = "http://127.0.0.1:8081/getFinishedTasks"

        useEffect(()=>{
            fetch(api_url)
            .then(response => response.json())
            .then(setList);
        }, []);
  

    return (   
        <div style={{display: 'flex', flexDirection: 'row', width: '25%'}}>
           {list.map(item => <TaskItem id={item.task_id} description={item.description} type={props.type} />)}
         </div>
       );
    
}

export default List;
