import React from "react";
import {useEffect, useState} from 'react';
import TaskItem from "./TaskItem";

function List(props) {

    const [list, setList] = useState([]);
    
    // var list;

    const api_url = props.status === "unfinished" ? "http://127.0.0.1:8081/getUnfinishedTasks" : 
                    props.status === "finished"   ? "http://127.0.0.1:8081/getFinishedTasks"   : ""

    
    function refreshList() {
      fetch(api_url)
      .then(response => response.json())
      .then(data => setList(() => list = data), console.log(list));
      console.log(list);
    }

    refreshList();

    return (   
        <div style={{display: 'flex', flexDirection: 'column', flexWrap: 'wrap'}}>
           { list.map ( item => <TaskItem id={item.task_id} description={item.description} status={props.status} refreshFunction = {refreshList}/> ) }
         </div>
       );
    
}

export default List;
