function AddToDoForm() {

    function sendData() {
        fetch('http://127.0.0.1:8081/addtodo', { 

        method: 'POST', 
        mode: 'cors', 
        body: JSON.stringify(document.getElementById("inp_todo").value) // body data type must match "Content-Type" header

    })

    return false;
  }

  return (
        <form onsubmit = "sendData()" method="post">
            <label>To-do: 
            <input type="text" name="todo" id="inp_todo"/>
            </label>
            <button type="submit" value="submit">submit</button>
        </form>
