import React, { Component } from "react";

class AddToDoForm extends Component {

    constructor(props) {
        super(props);
        this.state = {
          values: {
            task: ""
          },
          isSubmitting: false,
          isError: false
        };
      }

    submitForm = e => {
        console.log("submit form called");
        e.preventDefault();
        this.setState({ isSubmitting: true });
        console.log(this.state.values);
        fetch("http://127.0.0.1:8081/addtodo", {
            method: "POST",
            body: JSON.stringify(this.state.values),
            headers: {
                "Content-Type": "application/json"
            }
        })
        .then(res => {
            this.setState({ isSubmitting: false });
            return res.json();
        })
        .then(data => {
            console.log(data);
            if(!data.hasOwnProperty("error")){
                this.setState({ message: data.success });
                // re-render list component here
            }
            else {
                this.setState({ message: data.error, isError: true });
            }


        });

    }

    handleInputChange = e =>
    this.setState({
      values: { ...this.state.values, [e.target.name]: e.target.value }
    });


    // submitForm = (e) => {
    //     e.preventDefault();
    //     console.log(this.state.values);
    //     console.log("add task called");
    //     // var task = document.getElementById("inp_task").value;

    //     const requestOptions = {
    //         method: 'POST',
    //         headers: { 'Content-Type': 'application/json' },
    //         body: {
    //             "task_inp_value": task
    //            }
    //     };

    //     fetch('http://127.0.0.1:8081/addtodo', requestOptions)
    //         .then(function(response) {
    //             if(response.ok) {
    //                 alert('success!!');
    //             }
    //         throw new Error('Network response was not ok.');
    //         });
    // }

    render() {
        return (
            <form onSubmit={this.submitForm}>
                <label>Add a new task here : 
                <input type="text" name="task" id = "task" value={this.state.values.task} onChange={this.handleInputChange}/>
                </label>
                <button type="submit">Add</button>
            </form>
        );
    }
}  

export default AddToDoForm;
