import React from "react";
import {useEffect, useState, useRef} from 'react';
import TaskItem from "./TaskItem";

function List(props) {

    
    const [list, setList] = useState([]);
    
    const api_url = props.status === "unfinished" ? "http://127.0.0.1:8081/getUnfinishedTasks" : 
                    props.status === "finished"   ? "http://127.0.0.1:8081/getFinishedTasks"   : ""


    useEffect(()=>{
        fetch(api_url)
        .then(response => response.json())
        .then(setList);
    }, []);
  

    return (   
        <div style={{display: 'flex', flexDirection: 'column', flexWrap: 'wrap'}}>
           { list.map ( item => <TaskItem id={item.task_id} description={item.description} status={props.status} /> ) }
         </div>
       );
    
}

export default List;
