import React from "react";


function AddToDoForm() {

    function addTask() {
        console.log("add task called");
        var task = document.getElementById("inp_task").value;

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: {
                "task_inp_value": task
               }
        };

        fetch('http://127.0.0.1:8081/addtodo', requestOptions)
            .then(function(response) {
                if(response.ok) {
                    alert('success!!');
                }
            throw new Error('Network response was not ok.');
            });
    }

    return (
        <form method="post">
            <label>Add a new task here : 
            <input type="text" name="todo" id = "inp_task"/>
            </label>
            <button type="button" value="button" onClick="addTask">Add</button>
        </form>
    );
}  

export default AddToDoForm;
