import React, { Component } from "react";
import ReactDOM from 'react-dom'
import List from "./List";

function TaskItem(props) {

        const checked = props.type === "finished" ? true : false
        const lineStyle = props.type === "finished" ? "line-through" : 
        
        function handleDelete() {

            fetch("http://127.0.0.1:8081/deletethis", {
                method: "PUT",
                headers:{
                    'Content-Type':'application/json'
                    },
                body: JSON.stringify({task_id: props.id})
            });

        }

        function handleEdit() {
            var new_task = prompt("", props.description);
            fetch("http://127.0.0.1:8081/updatethis", {
                method: "PUT",
                headers:{
                    'Content-Type':'application/json'
                    },
                body: JSON.stringify({task_id: props.id, new_task: new_task})
            });
        }
        
        return (
           
            <div style={{border: '1px solid grey', margin: '10px', padding: '3px', 
            paddingRight: '5px', paddingBottom: '5px'}}  key={props.id} >
                <input  type={'checkbox'} key={props.id} checked={checked}/>  
                <span style={{textDecoration: lineStyle}}>{props.description}</span>
                <button className={'modifybuttons'} onClick={handleEdit}>edit</button>
                <button className={'modifybuttons'} onClick={handleDelete}>del</button>
            </div>
        
        );

}

export default TaskItem;
