import React,  { useState } from "react";
import ReactDOM from 'react-dom'
import List from "./List";

function useForceUpdate(props, status_id) {
    console.log("handling check")

            fetch("http://127.0.0.1:8081/changestatus", {
                method: "PUT",
                headers:{
                    'Content-Type':'application/json'
                    },
                body: JSON.stringify({task_id: props.id, status_id: status_id})
            });

            // window.location.reload(false);
            // useForceUpdate();
    
    let [value, setState] = useState(true);
    return () => setState(!value);
  }

function TaskItem(props) {

        const checked = props.type === "finished" ? true : false
        const lineStyle = props.type === "finished" ? "line-through" : ""
        const status_id = props.type === "finished" ? 1 : 0
        
        const handleCheck = useForceUpdate(props, status_id);

        // function handleCheck() {

            

        // }

        function handleDelete() {

            fetch("http://127.0.0.1:8081/deletethis", {
                method: "PUT",
                headers:{
                    'Content-Type':'application/json'
                    },
                body: JSON.stringify({task_id: props.id})
            });

            window.location.reload(false);

        }

        function handleEdit() {
            var new_task = prompt("", props.description);
            fetch("http://127.0.0.1:8081/updatethis", {
                method: "PUT",
                headers:{
                    'Content-Type':'application/json'
                    },
                body: JSON.stringify({task_id: props.id, new_task: new_task})
            });

            window.location.reload(false);

        }
        
        return (
           
            <div style={{margin: '10px', border: '1px solid grey', whiteSpace: 'nowrap'}}  key={props.id} >
                <span style={{textDecoration: lineStyle}}>{props.description}</span>
                <button className={'modifybuttons'} onClick={handleEdit}>edit</button>
                <button className={'modifybuttons'} onClick={handleDelete}>del</button>
                <input  type={'checkbox'} key={props.id} defaultChecked={checked} onChange={handleCheck}/>  

            </div>
        
        );

}

export default TaskItem;
